ht.columns.drawCell:
__ht__function(g, text, rect, option) {
            var font = "16px Arial",
                fontColor = "rgb(255, 255, 255)",
                bgColor = "rgb(133,158,212)",
                x = rect.x,
                y = rect.y,
                width = rect.width,
                height = rect.height,
                // 省还是市
                type = option.row.areaType,
                // 是否是展开状态
                expand = option.row.expand;
            if (type === 'province') {
                // 绘制背景
                g.fillStyle = bgColor;
                g.fill();
                if (expand) {
                    // 展开则绘制向上箭头
                    var image = ht.Default.getImage('symbols/疫情地图/upTriangle.json');
                    ht.Default.drawStretchImage(g, image, 'uniform', x + 15, y + 10 , 10, 10);
                } else if(!expand) {
                    // 没有展开绘制向右箭头
                    var image = ht.Default.getImage('symbols/疫情地图/rightTriangle.json');
                    ht.Default.drawStretchImage(g, image, 'uniform', x + 15, y + 10 , 10, 10);
                }
                // 绘制该表格文字内容
                ht.Default.drawText(g, text, font, fontColor, x , y, width, height, 'middle', 'middle');
            }  else if( type === 'city') {
                // 绘制该表格默认文字内容
                option.defaultDraw();
                // 绘制箭头
                // var image = ht.Default.getImage('symbols/疫情地图/rightArrow.json');
                // ht.Default.drawStretchImage(g, image, 'uniform', x + 75, y + 8 , 12, 12);
            }
        }

